import React, { useState } from "react";
import InputControl from "../InputControl/InputControl";
import { Link, useHistory } from "react-router-dom";
import { createUserWithEmailAndPassword, updateProfile } from "firebase/auth";
import { auth } from "../firebase";

function SignUp() {
  const navigate = useHistory();
  const [values, setValues] = useState({
    name: "",
    email: "",
    pass: "",
    stream: "",
  });
  const [errorMsg, setErrorMsg] = useState("");
  const [submitButtonDisabled, setSubmitButtonDisabled] = useState(false);

  const handleSubmission = () => {
    //CHECKING IF THE USER HAS ENTERED ALL THE DETAILS OR NOT
    if (!values.name || !values.email || !values.pass || !values.stream) {
      setErrorMsg("Fill all fields");
      return;
    }
    setErrorMsg("");

    setSubmitButtonDisabled(true);
    // THIS CREATEUSERWITHEMAILANDPASSWORD PROVIDES TO AUTHENTICATE THE MAIL AND PASSWORD BY THE FIREBASE
    createUserWithEmailAndPassword(auth, values.email, values.pass)
      .then(async (res) => {
        setSubmitButtonDisabled(false);
        // FOR DISPLAYING NAME PROPERTY
        const user = res.user;
        await updateProfile(user, {
          displayName: values.name,
        });
        navigate("/");
      })
      .catch((err) => {
        setSubmitButtonDisabled(false);
        setErrorMsg(err.message);
      });
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '90vh', backgroundColor: '#333' }}>
      <div style={{ width: '80%', maxWidth: '500px', display: 'flex', flexDirection: 'column', justifyContent: 'space-around', padding: '20px', borderRadius: '10px', backgroundColor: '#444', boxShadow: '0 0 10px rgba(0, 0, 0, 0.2)', color: '#fff' }}>
        <h1 style={{ color:'#3498db', textAlign: 'center' }}>Signup</h1>

        <InputControl
          label="Name"
          placeholder="Enter your name"
          onChange={(event) =>
            setValues((prev) => ({ ...prev, name: event.target.value }))
          }
          style={{ marginBottom: '15px', color: '#fff', backgroundColor: '#555', borderRadius: '5px', padding: '10px' }}
        />
        <InputControl
          label="Email"
          placeholder="Enter email address"
          onChange={(event) =>
            setValues((prev) => ({ ...prev, email: event.target.value }))
          }
          style={{ marginBottom: '15px', color: '#fff', backgroundColor: '#555', borderRadius: '5px', padding: '10px' }}
        />
        <InputControl
          label="Password"
          placeholder="Enter password"
          onChange={(event) =>
            setValues((prev) => ({ ...prev, pass: event.target.value }))
          }
          style={{ marginBottom: '15px', color: '#fff', backgroundColor: '#555', borderRadius: '5px', padding: '10px' }}
        />

        <div style={{ textAlign: 'center' }}>
          <b style={{color:'red', fontSize:'12px', display: 'block', marginBottom: '10px' }}>{errorMsg}</b>
          <button onClick={handleSubmission} disabled={submitButtonDisabled} className="btn btn-primary" style={{ marginBottom: '10px', backgroundColor: '#3498db', border: 'none', padding: '10px', borderRadius: '5px', color: '#fff' }}> 
            Signup
          </button>
          <p style={{ color: '#bbb' }}>
            Already have an account?{" "}
            <span>
              <Link to="/login" style={{ color: '#3498db' }}>LogIn</Link>
            </span>
          </p>
        </div>
      </div>
    </div>
  );
}

export default SignUp;
