import React, { useState } from "react";
import styles from "./SignUp.css";
import InputControl from "../InputControl/InputControl";
import { Link, useHistory } from "react-router-dom";
import { createUserWithEmailAndPassword, updateProfile } from "firebase/auth";
import { auth } from "../../firebase";

function SignUp() {
  const navigate = useH();
  const [values, setValues] = useState({
    name: "",
    email: "",
    pass: "",
    stream: "",
  });
  const [errorMsg, setErrorMsg] = useState("");
  const [submitButtonDisabled, setSubmitButtonDisabled] = useState(false);

  const handleSubmission = () => {
    //CHECKING IF THE USER HAS ENTERED ALL THE DETAILS OR NOT
    if (!values.name || !values.email || !values.pass || !values.stream) {
      setErrorMsg("Fill all fields");
      return;
    }
    setErrorMsg("");

    setSubmitButtonDisabled(true);
    // THIS CREATEUSERWITHEMAILANDPASSWORD PROVIDES TO AUTHENTICATE THE MAIL AND PASSWORD BY THE FIREBASE
    createUserWithEmailAndPassword(auth, values.email, values.pass)
      .then(async (res) => {
        setSubmitButtonDisabled(false);
        // FOR DISPLAYING NAME PROPERTY
        const user = res.user;
        await updateProfile(user, {
          displayName: values.name,
        });
        navigate("/");
      })
      .catch((err) => {
        setSubmitButtonDisabled(false);
        setErrorMsg(err.message);
      });
  };

  return (
    <div className={styles.container}>
      <div className={styles.innerBox}>
        <h1 className={styles.heading}>Signup</h1>

        <InputControl
          label="Name"
          placeholder="Enter your name"
          onChange={(event) =>
            setValues((prev) => ({ ...prev, name: event.target.value }))
          }
        />
        <InputControl
          label="Email"
          placeholder="Enter email address"
          onChange={(event) =>
            setValues((prev) => ({ ...prev, email: event.target.value }))
          }
        />
        <InputControl
          label="Password"
          placeholder="Enter password"
          onChange={(event) =>
            setValues((prev) => ({ ...prev, pass: event.target.value }))
          }
        />

        <div className={styles.dropdownContainer}>
          <label htmlFor="stream">Stream</label>
          <select
            id="stream"
            name="stream"
            // value={stream}
            onChange={(event) =>
              setValues((prev) => ({ ...prev, stream: event.target.value }))
            }
          >
            <option value="">Select Stream</option>
            <option value="engineering">Engineering</option>
            <option value="medical">Medical</option>
            <option value="commerce">Commerce</option>
          </select>
        </div>

        <div className={styles.footer}>
          <b className={styles.error}>{errorMsg}</b>
          <button onClick={handleSubmission} disabled={submitButtonDisabled}>
            Signup
          </button>
          <p>
            Already have an account?{" "}
            <span>
              <Link to="/SignIn">SignIn</Link>
            </span>
          </p>
        </div>
      </div>
    </div>
  );
}

export default SignUp;

