import React, { useState } from "react";
// import styles from "./Signup.css";
import InputControl from "../InputControl/InputControl";
import { Link, useHistory } from "react-router-dom";
import { createUserWithEmailAndPassword, updateProfile } from "firebase/auth";
import { auth } from "../firebase";
import Form from 'react-bootstrap/Form';

function SignUp() {
  const navigate = useHistory();
  const [values, setValues] = useState({
    name: "",
    email: "",
    pass: "",
    stream: "",
  });
  const [errorMsg, setErrorMsg] = useState("");
  const [submitButtonDisabled, setSubmitButtonDisabled] = useState(false);

  const handleSubmission = () => {
    //CHECKING IF THE USER HAS ENTERED ALL THE DETAILS OR NOT
    if (!values.name || !values.email || !values.pass || !values.stream) {
      setErrorMsg("Fill all fields");
      return;
    }
    setErrorMsg("");

    setSubmitButtonDisabled(true);
    // THIS CREATEUSERWITHEMAILANDPASSWORD PROVIDES TO AUTHENTICATE THE MAIL AND PASSWORD BY THE FIREBASE
    createUserWithEmailAndPassword(auth, values.email, values.pass)
      .then(async (res) => {
        setSubmitButtonDisabled(false);
        // FOR DISPLAYING NAME PROPERTY
        const user = res.user;
        await updateProfile(user, {
          displayName: values.name,
        });
        navigate("/");
      })
      .catch((err) => {
        setSubmitButtonDisabled(false);
        setErrorMsg(err.message);
      });
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '100vh', backgroundColor: '#f5f5f5' }}>
      <div style={{ width: '300px', display: 'flex', flexDirection: 'column', justifyContent: 'center', padding: '20px', borderRadius: '10px', backgroundColor: '#fff', boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)' }}>
        <h1 style={{ color:'#3498db', textAlign: 'center', marginBottom: '20px' }}>Signup</h1>

        <InputControl
          label="Name"
          placeholder="Enter your name"
          onChange={(event) =>
            setValues((prev) => ({ ...prev, name: event.target.value }))
          }
          style={{ marginBottom: '10px' }}
        />
        {/* ...other InputControl components... */}
        
        <Form.Select aria-label="Default select example" style={{ maxWidth: '500px', backgroundColor: '#fff', border: '1px solid #ccc', borderRadius: '5px', marginBottom: '10px' }}
          // ...other attributes...
        >
          {/* ...options... */}
        </Form.Select>

        <div style={{ textAlign: 'center', marginTop: '10px' }}>
          <b style={{ color: 'red', fontSize: '10px', marginRight: '3px', fontWeight: '500', display: 'block', marginBottom: '10px' }}>{errorMsg}</b>
          <button onClick={handleSubmission} disabled={submitButtonDisabled} className="btn btn-primary" style={{ marginBottom: '10px' }}> 
            Signup
          </button>
          <p>
            Already have an account?{" "}
            <span>
              <Link to="/login">LogIn</Link>
            </span>
          </p>
        </div>
      </div>
    </div>
  );
}

export default SignUp;
