import React, { useState } from "react";
// import styles from "./Signup.css";
import InputControl from "../InputControl/InputControl";
import { Link, useHistory } from "react-router-dom";
import { createUserWithEmailAndPassword, updateProfile } from "firebase/auth";
import { auth } from "../firebase";
import Form from 'react-bootstrap/Form';

function SignUp() {
  const navigate = useHistory();
  const [values, setValues] = useState({
    name: "",
    email: "",
    pass: "",
    stream: "",
  });
  const [errorMsg, setErrorMsg] = useState("");
  const [submitButtonDisabled, setSubmitButtonDisabled] = useState(false);

  const handleSubmission = () => {
    //CHECKING IF THE USER HAS ENTERED ALL THE DETAILS OR NOT
    if (!values.name || !values.email || !values.pass || !values.stream) {
      setErrorMsg("Fill all fields");
      return;
    }
    setErrorMsg("");

    setSubmitButtonDisabled(true);
    // THIS CREATEUSERWITHEMAILANDPASSWORD PROVIDES TO AUTHENTICATE THE MAIL AND PASSWORD BY THE FIREBASE
    createUserWithEmailAndPassword(auth, values.email, values.pass)
      .then(async (res) => {
        setSubmitButtonDisabled(false);
        // FOR DISPLAYING NAME PROPERTY
        const user = res.user;
        await updateProfile(user, {
          displayName: values.name,
        });
        navigate("/");
      })
      .catch((err) => {
        setSubmitButtonDisabled(false);
        setErrorMsg(err.message);
      });
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '100vh', backgroundColor: 'black' }}>
      <div style={{ width: '300px', display: 'flex', flexDirection: 'column', justifyContent: 'center', padding: '20px', borderRadius: '10px', backgroundColor: '#fff', boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)' }}>
        <h1 style={{color:'#3498db'}}>Signup</h1>

        <InputControl
          label="Name"
          placeholder="Enter your name"
          onChange={(event) =>
            setValues((prev) => ({ ...prev, name: event.target.value }))
          }
        />
        <InputControl
          label="Email"
          placeholder="Enter email address"
          onChange={(event) =>
            setValues((prev) => ({ ...prev, email: event.target.value }))
          }
        />
        <InputControl
          label="Password"
          placeholder="Enter password"
          onChange={(event) =>
            setValues((prev) => ({ ...prev, pass: event.target.value }))
          }
        />
        <Form.Select aria-label="Default select example" style={{ maxWidth: '500px'}} className='mx-auto mb-3'
          id="stream"
          name="stream"
          onChange={(event) =>
            setValues((prev) => ({ ...prev, stream: event.target.value }))
          }>
          <option value="">Select Stream</option>
          <option value="Computer Science Engineering">Computer Science Engineering</option>
          <option value="Civil Engineering">Civil Engineering</option>
          <option value="Mechanical Engineering">Mechanical Engineering</option>
          <option value="Electrical Engineering">Electrical Engineering</option>
          <option value="Chemical Engineering">Chemical Engineering</option>
          <option value="Aerospace Engineering">Aerospace Engineering</option>
        </Form.Select>

        <div>
          <b style={{color:'red',fontSize:'10px', marginRight:'3px', fontWeight:'500'}} >{errorMsg}</b>
          <button onClick={handleSubmission} disabled={submitButtonDisabled} className="btn btn-primary"> 
            Signup
          </button>
          <p>
            Already have an account?{" "}
            <span>
              <Link to="/login">LogIn</Link>
            </span>
          </p>
        </div>
      </div>
    </div>
  );
}

export default SignUp;
